import React, { useState, useEffect } from 'react';
import './styles.css'; // Import the CSS file

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [searchCity, setSearchCity] = useState('');
  const [searchState, setSearchState] = useState('');

  useEffect(() => {
    // Fetch data from backend API endpoint 
    const queryParams = new URLSearchParams({
      city: searchCity,
      state: searchState
    }).toString();

    fetch(`http://localhost:8080/api/pets?${queryParams}`)  
      .then(response => response.json())
      .then(data => {
        // Assume data is pre-filtered for duplicates by backend for simplicity
        setPets(data);
      })
      .catch(error => console.error(error));
  }, [searchCity, searchState]); // Depend on searchCity and searchState to re-fetch when they change

  const [expandedPetId, setExpandedPetId] = useState(null);

  const handleToggleDescription = (petId) => {
    setExpandedPetId(expandedPetId === petId ? null : petId);
  };

  const handleSearchCityChange = (event) => {
    setSearchCity(event.target.value);
  };

  const handleSearchStateChange = (event) => {
    setSearchState(event.target.value);
  };

  return (
    <div className="pet-list-container">
      <h1>Pet List</h1>
      <div className="search-bar">
        <div>
          <label htmlFor="searchCity">City:</label>
          <input
            type="text"
            id="searchCity"
            value={searchCity}
            onChange={handleSearchCityChange}
          />
        </div>
        <div>
          <label htmlFor="searchState">State:</label>
          <input
            type="text"
            id="searchState"
            value={searchState}
            onChange={handleSearchStateChange}
          />
        </div>
      </div>
      {searchCity && searchState && (
        <p className="search-location-indicator">
          Showing results for: <strong>{searchCity}, {searchState}</strong>
        </p>
      )}
      <div className="horizontal-pet-list">
        {pets.map(pet => (
          <div key={pet.id} className="pet-card">
            <h2>{pet.name}</h2>
            <p>Location: {pet.location}</p> {/* Display pet location */}
            <div className="image-container">
              {pet.images.length > 0 && (
                <img src={pet.images[0]} alt={`Pet ${pet.name}`} />
              )}
            </div>
            <button onClick={() => handleToggleDescription(pet.id)}>
              {expandedPetId === pet.id ? 'Hide Description' : 'Show Description'}
            </button>
            {expandedPetId === pet.id && (
              <p>{pet.description}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PetList;
