import React, { useState, useEffect } from 'react';
import './styles.css'; // Import the CSS file, assuming similar styling

const PetCareSearch = () => {
  const [providers, setProviders] = useState([]);
  const [searchLocation, setSearchLocation] = useState('');

  useEffect(() => {
    if (searchLocation) {
      // Fetch data from backend API endpoint with the searchLocation parameter
      fetch(`http://localhost:8080/api/yelp/search?location=${searchLocation}&term=pet care`)  // Update the URL based on your backend setup
        .then(response => response.json())
        .then(data => setProviders(data.businesses))  // Assuming the backend structure aligns with Yelp's response
        .catch(error => console.error('Error:', error));
    }
  }, [searchLocation]);

  const [expandedProviderId, setExpandedProviderId] = useState(null);

  const handleToggleDescription = (providerId) => {
    setExpandedProviderId(expandedProviderId === providerId ? null : providerId);
  };

  const handleSearchLocationChange = (event) => {
    setSearchLocation(event.target.value);
  };

  return (
    <div className="pet-care-container">
      <h1>Pet Care Providers</h1>
      <div className="search-bar">
        <label htmlFor="searchLocation">Search Location:</label>
        <input
          type="text"
          id="searchLocation"
          value={searchLocation}
          onChange={handleSearchLocationChange}
        />
      </div>
      {searchLocation && (
        <p className="search-location-indicator">
          Showing results for: <strong>{searchLocation}</strong>
        </p>
      )}
      <div className="horizontal-provider-list">
        {providers.map(provider => (
          <div key={provider.id} className="provider-card">
            <h2>{provider.name}</h2>
            <p>Location: {provider.location.address1}, {provider.location.city}</p>
            <div className="image-container">
              {provider.image_url && (
                <img src={provider.image_url} alt={`Provider ${provider.id}`} />
              )}
            </div>
            <button onClick={() => handleToggleDescription(provider.id)}>
              {expandedProviderId === provider.id ? 'Hide Details' : 'Show Details'}
            </button>
            {expandedProviderId === provider.id && (
              <div>
                <p>{provider.location.address1}, {provider.location.city}</p>
                <p>Rating: {provider.rating}</p>
                <p>Phone: {provider.display_phone}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PetCareSearch;
