import React, { useState } from 'react';

function ResetPassword({ match }) { // Assuming you are using React Router for routing
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const { id, token } = match.params; // Extracting id and token from the URL

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Assuming you have an endpoint '/api/auth/reset-password/:id/:token' for this functionality
    const response = await fetch(`/api/auth/reset-password/${id}/${token}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ password }),
    });
    const data = await response.json();
    setMessage(data.message);
  };

  return (
    <div>
      <h2>Reset Password</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>New Password:</label>
          <input
            type="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Reset Password</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default ResetPassword;
