import React, { useState } from 'react';
import axios from 'axios';

const YelpSearch = () => {
  // State variables to hold the location, search term, and businesses fetched from the API
  const [location, setLocation] = useState('');
  const [term, setTerm] = useState('');
  const [businesses, setBusinesses] = useState([]);

  // handleSearch is an asynchronous function triggered on button click to fetch data from the backend
  const handleSearch = async () => {
    try {
      // Making a GET request to the backend API with location and term as parameters
      const response = await axios.get('http://localhost:8080/api/yelp/search', {
        params: { location, term: term || 'pet care' } 
      });
      // Setting the businesses state with the data received from the API response
      setBusinesses(response.data.businesses);
    } catch (error) {
      // Logging any errors that occur during the fetch operation
      console.error('Error fetching data:', error);
    }
  };

  // The component's render method returns the UI elements
  return (
    <div>
      <h1>Find Pet Care Services</h1>
      <div>
        {/* Input fields for term and location, with their respective state-updating functions */}
        <input
          type="text"
          placeholder="Search term (e.g., 'dog grooming')"
          value={term}
          onChange={(e) => setTerm(e.target.value)}
        />
        <input
          type="text"
          placeholder="Location (e.g., 'San Francisco')"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        {/* Button to initiate the search operation */}
        <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        {/* Conditionally rendering search results or a 'No results found' message */}
        {businesses.length > 0 ? (
          <ul>
            {/* Mapping over the businesses array to render each business's details */}
            {businesses.map((business) => (
              <li key={business.id}>
                <h3>{business.name}</h3>
                <p>{business.location.address1}, {business.location.city}</p>
                <p>{business.phone}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
};

// Exporting the YelpSearch component for use in other parts of the application
export default YelpSearch;
