import React, { useState } from 'react';
import axios from 'axios';

function PetCareSearch() {
  const [term, setTerm] = useState('');
  const [location, setLocation] = useState('');
  const [providers, setProviders] = useState([]);

  const searchProviders = async () => {
    try {
      const response = await axios.get('/api/yelp/search', {
        params: {
          term: term || 'pet care', // Default to 'pet care' if no term is provided
          location: location,
          limit: 10 // You can adjust the limit as needed
        }
      });
      setProviders(response.data.businesses);
    } catch (error) {
      console.error('Error fetching data from Yelp API:', error);
      // You can add more sophisticated error handling here
    }
  };

  return (
    <div>
      <h2>Search for Pet Care Providers</h2>
      <input
        type="text"
        placeholder="Search term (e.g., 'dog walking')"
        value={term}
        onChange={(e) => setTerm(e.target.value)}
      />
      <input
        type="text"
        placeholder="Location (e.g., 'San Francisco')"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={searchProviders}>Search</button>
      <div>
        {providers.length > 0 ? (
          <ul>
            {providers.map((provider) => (
              <li key={provider.id}>
                <h3>{provider.name}</h3>
                <p>{provider.location.address1}</p>
                <p>{provider.location.city}, {provider.location.state} {provider.location.zip_code}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No providers found</p>
        )}
      </div>
    </div>
  );
}

export default PetCareSearch;
