// PetCareProviders.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PetCareProviders = () => {
  const [petCareProviders, setPetCareProviders] = useState([]);
  const [searchLocation, setSearchLocation] = useState('');

  useEffect(() => {
    // Fetch pet care providers from your backend API endpoint with location and term
    fetchPetCareProviders();
  }, [searchLocation]);

  const fetchPetCareProviders = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/pet-care-providers`, {
        params: {
          location: searchLocation,
          term: 'pet care', // Specify the desired term for pet care providers
        },
      });

      setPetCareProviders(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSearchLocationChange = (event) => {
    setSearchLocation(event.target.value);
  };

  return (
    <div className="pet-list-container">
      <h1>Pet Care Providers</h1>
      <div className="search-bar">
        <label htmlFor="searchLocation">Search Location:</label>
        <input
          type="text"
          id="searchLocation"
          value={searchLocation}
          onChange={handleSearchLocationChange}
        />
      </div>
      {searchLocation && (
        <p className="search-location-indicator">
          Showing results for: <strong>{searchLocation}</strong>
        </p>
      )}
      <div className="horizontal-pet-list">
        {petCareProviders.map(provider => (
          <div key={provider.id} className="pet-card">
            <h2>{provider.name}</h2>
            <p>Rating: {provider.rating}</p>
            <p>Location: {provider.location}</p>
            <p>Phone: {provider.phone}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PetCareProviders;
