import React, { useState, useEffect } from 'react';
import jwt from 'jsonwebtoken';
import './styles.css'; // Import the CSS file


// import { useAuth } from '../auth/AuthProvider';




const PetList = () => {
  const token = localStorage.getItem("token");
  const decodedToken = jwt.decode(token);
  const user = decodedToken.id;

  const [pets, setPets] = useState([]);
  const [searchLocation, setSearchLocation] = useState('');
  const [selectedPetId, setSelectedPetId] = useState(null);
  const [isSavingFavorite, setIsSavingFavorite] = useState(false);

  useEffect(() => {
    // Fetch data from your backend API endpoint with the searchLocation parameter
    fetch(`http://localhost:8080/api/pets?location=${searchLocation}`)
      .then(response => response.json())
      .then(data => setPets(data))
      .catch(error => console.error(error));
  }, [searchLocation]);

  const handleSaveFavorite = async () => {
    try {
      if (user && selectedPetId) {
        setIsSavingFavorite(true);

        const userId = user.id;
        const petId = selectedPetId;

        // Call the backend to save the favorite pet ID
        const response = await fetch('http://localhost:8080/api/users/saveFavorite', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ userId, petId }),
        });

        const data = await response.json();

        if (response.ok) {
          console.log(data.message);
        } else {
          console.error(data.message);
        }

        setIsSavingFavorite(false);
      } else {
        console.error('User not authenticated or no pet selected.');
      }
    } catch (error) {
      console.error('Error saving favorite pet:', error);
      setIsSavingFavorite(false);
    }
  };

  const handleSelectPet = (petId) => {
    setSelectedPetId(petId);
  };

  const handleSearchLocationChange = (event) => {
    setSearchLocation(event.target.value);
  };

  return (
    <div className="pet-list-container">
      <h1>Pet List</h1>
      <div className="search-bar">
        <label htmlFor="searchLocation">Search Location:</label>
        <input
          type="text"
          id="searchLocation"
          value={searchLocation}
          onChange={handleSearchLocationChange}
        />
      </div>
      <div className="horizontal-pet-list">
        {pets.map(pet => (
          <div key={pet.id} className={`pet-card ${pet.id === selectedPetId ? 'selected' : ''}`}>
            <h2>{pet.name}</h2>
            <p>Location: {pet.location}</p>
            <div className="image-container">
              {pet.images.length > 0 && (
                <img src={pet.images[0]} alt={`Pet ${pet.id}`} />
              )}
            </div>
            <button onClick={() => handleSelectPet(pet.id)}>
              Select
            </button>
          </div>
        ))}
      </div>
      {selectedPetId && (
        <div className="save-favorite">
          <button onClick={handleSaveFavorite} disabled={isSavingFavorite}>
            {isSavingFavorite ? 'Saving...' : 'Save as Favorite'}
          </button>
        </div>
      )}
    </div>
  );
};

export default PetList;
