import React, { useState } from 'react';
import './styles.css';

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [searchLocation, setSearchLocation] = useState('');
  const [expandedPetId, setExpandedPetId] = useState(null);

  const handleToggleDescription = (petId) => {
    setExpandedPetId(expandedPetId === petId ? null : petId);
  };

  const handleSearchLocationChange = (event) => {
    setSearchLocation(event.target.value);
  };

  const handleSearch = () => {
    // Only fetch data when the search button is clicked
    fetch(`http://localhost:8080/api/pets?location=${encodeURIComponent(searchLocation)}`)
      .then(response => response.json())
      .then(data => {
        setPets(data);
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="pet-list-container">
      <h1>Pet List</h1>
      <div className="search-bar">
        <input
          type="text"
          id="searchLocation"
          placeholder="Enter a location"
          value={searchLocation}
          onChange={handleSearchLocationChange}
        />
        <button onClick={handleSearch}>Search</button> {/* Search button */}
      </div>
      <div className="vertical-pet-list">
        {pets.length > 0 ? ( // Render pets only if there are pets in the state
          pets.map(pet => (
            <div key={pet.id} className="pet-card">
              <h2>{pet.name}</h2>
              <p>Location: {pet.location}</p>
              <div className="image-container">
                {pet.images && pet.images.length > 0 && (
                  <img src={pet.images[0]} alt={`Pet ${pet.name}`} />
                )}
              </div>
              <button onClick={() => handleToggleDescription(pet.id)}>
                {expandedPetId === pet.id ? 'Hide Description' : 'Show Description'}
              </button>
              {expandedPetId === pet.id && (
                <p className="pet-description">{pet.description}</p>
              )}
            </div>
          ))
        ) : (
          searchLocation && <p>No pets found for the specified location.</p> // Message when no pets are found
        )}
      </div>
    </div>
  );
};

export default PetList;
